/* Mobile version: */
*{
    font-family: roboto;
}

/* General */
body{
    margin:0px;
    background-color: #f4f3f1;
}
a{
    text-decoration: none;
    color:#433D86;
}
a:hover{
    color:#376b3d;
}
main {
    padding: 5vw;
}
.mybutton{
  background-color: #C0BBFF;
  padding:5px;
  border-radius: 10%;
  margin-top: 10px;
  cursor:pointer ;

}
cite{
    font-weight: 800;
}
h1 {
    font-family: frijole;
    font-size: 7vw;
}
blockquote{
    font-size: 20px;
    font-style: italic;
    color:#7e7d7d;
}
blockquote a {
    margin-left: 15px;
    font-weight: 400;

}
#next{
    position: relative;
    float:right;
    bottom: 46px;
    right:10px;
    font-weight: 800;

}
#previous{
    font-weight: 800;
    margin: 10px;
}


/* general table setting */

table{
    border-collapse:collapse;
    margin:auto;
}
td,th{
    padding:5px;
    border:1px solid #000;
}
table caption{
    color:#433D86;
    background-color:#C0BBFF;
    border: 1px solid #433D86;
}

/* Iframe video setting */
iframe{
    width: 80vw;
    height:50vw;
    /* max-width: 600px;
    max-height: 400px; */
}


/* header */

nav p {
    color:#f4f3f1;
    margin:auto;
    font-size:4vw;
}
nav h1 {
    color:#C0BBFF;
    margin:auto;
    font-size: 5vw;
}
#nav-options {
    background-color: #f4f3f1;
    text-align: center;
}
#nav-options ul{
    font-size: 4vw;
    list-style: none; 
    border-bottom:1px solid #000;
}
#nav-options ul li {
    margin: 3vw auto;
}

#nav-options ul li a {
    color:#433D86;
    font-weight:800;
}
#nav-options ul li a:hover{
    color:#376b3d;

}
#nav-options .here{
    color:#376b3d;
    cursor:default;
}
#title{
    padding:3vw;
    background-color:#000;
}

/* images: */
img{
    width: 80vw;
}

figure {
    margin:0;
    text-align: center;
}
.image-circle{
    border-radius: 50%;
    width: 300px;

}
figcaption{
    font-size:2vw;
    line-height: 15px;
}



/* Adult Content*/
.hidden{
    display:none;
}

/* footer */
footer{
    font-size: 12px;
    background-color:#000;
    color:#C0BBFF;
    padding:6vw 6vw 10vw 6vw;
    margin-top: 50px;
}
footer ul {
    list-style: none;
}
footer ul li a{
    color: #C0BBFF;
}
#goUp{
    position:relative;
    float:right;
    bottom: 50px;
    right:20px;
    font-size: 10vw;
}
p{
    text-align: justify;
}

/* form */
label input{
    margin:10px;
}


/* index */



/* book list */
#bookDisplay figure img{
    width: 80vw;
    height: 80vw;
}


