/* tablet version */

@media(min-width:700px){
    /* general */


    p{
        font-size: 20px;
    }
    h3{
        font-size:25px;
    }
    h2{
        font-size: 30px;
    }


    /* header */

nav p {
    font-size:28px;

}
nav h1 {
    font-size: 35px;

}
#nav-options {
    background-color: #000;
    text-align: left;
    height: 60px;
    height: 50px;


}
#nav-options ul{
    font-size: 20px;
    display: inline-flex;
    margin: 0;
    padding: 0;
    border:none;
    height: 50px;
}
#nav-options ul li {
    margin: 17px 0 auto 18px;
}

#nav-options ul li:nth-of-type(1) :after{
    content: "|";
    margin-left:8px;
    color:#f4f3f1;
}
#nav-options ul li:nth-of-type(2) :after{
    content: "|";
    margin-left:8px;
    color:#f4f3f1;
}

#nav-options ul li a {
    color:#f4f3f1;
    font-weight:400;
}
#title{
    padding:15px;
    height: 50px;

}

/* images */



img{

   width: 300px;

}


body section p +figure{

    position:relative;
    width: 320px;
    float: right;



}
body section p+figure +p+p{
    margin-top: 30px;
}


.image-circle{
    border-radius: 0%;
    width: 200px;
    height: 300px;


}
figcaption{
    font-size:10px;
    padding:10px;
}

.mainImage{
    position: relative;
    float: right;
    margin:10px 10px 120px 10px;
    top:130px;

}

/* index */




#index iframe{
   margin: auto 40px;
}
#index h3{
    text-align: left;
}



/* footer */
#goUp{
    font-size: 50px;
 }

 footer{

    padding:10px 10px 50px 10px ;
}


/* Book List */

#bookDisplay{
    width: 95vw;
    display:inline-flex;
    flex-wrap: wrap;

}
#bookDisplay section{
    text-align: center;

}
#bookDisplay figure{
    width:250px;
    padding:20px;
    margin:2.5vw;
}
#bookDisplay figure img{
    width:250px;
    height:300px;

}


/* personal life */
#personalLife .mainImage{
float:left;
top:0;
margin:0px 10px;
}
#personalLife section blockquote+p+figure+p+blockquote+p+figure{
    float:left;
}



/* Detail Book */

#detailFiction .mainImage, #detailNonFiction .mainImage{
width: 300px;
height:350px;
top:0;
margin: 10px 10px 10px 10px;
}
#detailFiction .mainImage img,#detailNonFiction .mainImage img{
width: 250px;
height:300px;
}

.inlineFlex{
    text-align: center;
}
.inlineFlex section{
    margin:auto;
}


}



/* pc / laptop */

@media (min-width:1200px){

    #bookDisplay figure{

        width:250px;
        padding:20px;
        margin:auto;
    }
    .inlineFlex{
        position:relative;
        display: inline-flex;
        flex-wrap: wrap;

    }
    .inlineFlex section{
        width:40vw;
        height: 500px;
    }
iframe{

        width:35vw;
        height: 25vw;
        max-width: 800px;
        max-height: 600px;
    }
    #personalLife section blockquote+p+figure+p+blockquote+p+figure{
    top: 0;
    }
    #personalLife section+section{
        margin-top: 10vw;
    }
    p+figure+p+p{
        margin-bottom: 10vw;
    }
    #personalLife p+figure+p+p{
        margin-bottom: auto;
    }

}
